import React, { useState } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';

const Aisuggest = ({ code }) => {
  const [suggestion, setSuggestion] = useState('');
  const [loading, setLoading] = useState(false);

  // Async function to fetch AI code suggestions
  const fetchAICodeSuggestions = async (codeSnippet) => {
    setLoading(true);
    try {
      const response = await axios.post(
        'https://api.openai.com/v1/chat/completions',
        {
          model: 'gpt-4o-mini', // Ensure this model is available to your API key
          messages: [
            {
              role: 'user',
              content: `Suggest improvements or corrections for the following code: ${codeSnippet}`
            }
          ]
        },
        {
          headers: {
            Authorization: `Bearer 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
`, // Replace with your API key from env variable
            'Content-Type': 'application/json'
          }
        }
      );

      // Extracting suggestion text from the response
      const suggestionText = response.data.choices?.[0]?.message?.content || 'No suggestion available.';
      setSuggestion(suggestionText);
      toast.success('AI suggestion received!');
    } catch (error) {
      handleError(error);
    } finally {
      setLoading(false);
    }
  };

  const handleError = (error) => {
    if (error.response) {
      // Server responded with a status other than 200
      if (error.response.status === 429) {
        toast.error('Rate limit exceeded. Please try again later.');
      } else if (error.response.status === 401) {
        toast.error('Unauthorized: Check your API key.');
      } else {
        toast.error(`Error: ${error.response.data?.error?.message || 'Something went wrong.'}`);
      }
    } else if (error.request) {
      // Request was made but no response was received
      toast.error('No response from server. Please check your network.');
    } else {
      // Something happened in setting up the request
      toast.error('Error in setting up request: ' + error.message);
    }
  };

  // Function to handle button click for fetching suggestions
  const handleGetSuggestions = () => {
    if (!code.trim()) {
      toast.error('Code editor is empty!');
      return;
    }
    fetchAICodeSuggestions(code);
  };

  return (
    <div className="bg-[#2e2f3e] p-4 rounded mt-4 text-white">
      <h3 className="text-lg font-semibold mb-2">AI Code Suggestions</h3>
      <button
        onClick={handleGetSuggestions}
        className="bg-blue-500 px-4 py-2 rounded hover:bg-blue-600 transition mb-4"
        disabled={loading}
      >
        {loading ? 'Fetching...' : 'Get AI Suggestions'}
      </button>
      {suggestion && (
        <div className="bg-[#1e1f29] p-3 rounded mt-2">
          <h4 className="text-md font-medium mb-1">Suggested Improvements:</h4>
          <pre>{suggestion}</pre>
        </div>
      )}
    </div>
  );
};

export default Aisuggest;
