import React, { useEffect } from 'react';
import { AiOutlineAudio } from "react-icons/ai";
import { ZegoUIKitPrebuilt } from '@zegocloud/zego-uikit-prebuilt';
import { useLocation } from 'react-router-dom';

const Audiocall = () => {
  const location = useLocation();
  const { username, roomid } = location.state || {};

  useEffect(() => {
    if (!roomid || !username) {
      console.error("Room ID and username must be provided");
    }
  }, [roomid, username]);

  const play = async (element) => {
    const appID = 1029814731; // Replace with your ZEGOCLOUD App ID
    const serverSecret = "487ec97a55397f781ce2a05b879b756c"; // Replace with your ZEGOCLOUD Server Secret
    const userID = String(roomid || "defaultRoomID"); // Convert to string
    const userName = String(username || "Guest"); // Convert to string

    // Generate a kit token
    const kitToken = ZegoUIKitPrebuilt.generateKitTokenForProduction(appID, serverSecret, roomid, userID, userName);

    // Create an instance and join the room
    const zc = ZegoUIKitPrebuilt.create(kitToken);
    zc.joinRoom({
      container: element,
      sharedLinks: [],
      scenario: {
        mode: ZegoUIKitPrebuilt.GroupCall, // Use GroupCall mode
      },
      userID, // Set userID
      userName, // Set userName
    });
  };

  return (
    <div>
      <button onClick={() => play(document.getElementById("call-container"))}>
        <AiOutlineAudio /> Voice and Video Call
      </button>
      <div id="call-container" style={{ width: '100%', height: '80vh' }}></div>
    </div>
  );
};

export default Audiocall;
