import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { CopyToClipboard } from "react-copy-to-clipboard";
import toast from "react-hot-toast";
import Avatar from "react-avatar";
import { Controlled as CodeMirror } from "react-codemirror2";
import Chat from "./Chat";
import "codemirror/lib/codemirror.css";
import "codemirror/mode/javascript/javascript";
import "codemirror/mode/python/python";
import "codemirror/mode/clike/clike";
import "codemirror/theme/dracula.css";
import socket from './Socket';
import Audiocall from "./Audiocall";
import Downloadbtn from "./Downloadbtn";
import axios from 'axios';

const Editor = () => {
  const location = useLocation();
  const { username, roomid } = location.state || {};
  const navigate = useNavigate();
  const [language, setLanguage] = useState("javascript");
  const [code, setCode] = useState("");
  const [output, setOutput] = useState("");
  const [loading, setLoading] = useState(false);
  const [user, setUser] = useState([]);
  const [coderun, setCoderun] = useState("Run");
  const [suggestion, setSuggestion] = useState('');

  // Join room logic and socket event listeners
  useEffect(() => {
    if (username && roomid) {
      socket.emit("join-room", { username, roomid });
    }
  }, [username, roomid]);

  // Fetch AI Suggestions function
  const fetchAICodeSuggestions = async (codeSnippet) => {
    try {
      const response = await axios.post(
        'https://api.openai.com/v1/chat/completions',
        {
          model: 'gpt-3.5-turbo',
          messages: [
            { role: 'user', content: `Suggest improvements or corrections for the following code: ${codeSnippet}` },
          ],
        },
        {
          headers: {
            'Authorization': 'Bearer YOUR_API_KEY', // Replace with your actual API key
            'Content-Type': 'application/json',
          },
        }
      );
      return response.data.choices[0].message.content;
    } catch (error) {
      console.error('Error fetching AI suggestions:', error);
      toast.error("Failed to fetch suggestions.");
      return null;
    }
  };

  const handleGetSuggestions = async () => {
    const codeSnippet = code;
    const suggestion = await fetchAICodeSuggestions(codeSnippet);
    if (suggestion) {
      setSuggestion(suggestion);
      toast.success("AI suggestions fetched successfully!");
    }
  };

  // Other socket listeners and code logic

  return (
    <div className="flex min-h-screen bg-[#1c1e29] text-white">
      {/* Sidebar - User Info and Controls */}
      <div className="w-1/4 p-5 flex flex-col items-center border-r border-gray-700">
        {/* Room ID, Copy Button, Leave Room, User List */}
        <CopyToClipboard text={roomid} onCopy={() => toast.success("Room ID copied!")}>
          <button className="bg-blue-500 px-4 py-2 rounded hover:bg-blue-600 transition mb-4">
            Copy Room ID
          </button>
        </CopyToClipboard>
        <button onClick={() => { socket.disconnect(); navigate("/"); toast.success("Left the room"); }}
          className="bg-red-500 px-4 py-2 rounded hover:bg-red-600 transition">
          Leave Room
        </button>
      </div>
      
      {/* Chat and Audiocall Components */}
      <Chat />
      <Audiocall />

      {/* Main Editor Section */}
      <div className="w-3/4 p-5">
        <div className="mb-4">
          <label htmlFor="language" className="block mb-2 font-medium">
            Choose Language
          </label>
          <select
            id="language"
            onChange={(e) => setLanguage(e.target.value)}
            value={language}
            className="w-full bg-[#2e2f3e] text-white p-2 rounded outline-none border border-gray-700"
          >
            <option value="javascript">JavaScript</option>
            <option value="python">Python</option>
            <option value="text/x-csrc">C</option>
            <option value="text/x-c++src">C++</option>
            <option value="text/typescript">TypeScript</option>
          </select>
        </div>

        <Downloadbtn code={code} language={language} />

        {/* Code Editor */}
        <CodeMirror
          value={code}
          onBeforeChange={(editor, data, value) => {
            setCode(value);
            socket.emit("code-change", { roomid, code: value });
          }}
          options={{
            theme: "dracula",
            lineNumbers: true,
            mode: language,
            extraKeys: { "Ctrl-Space": "autocomplete" },
            matchBrackets: true,
          }}
          className="border border-gray-700 rounded mb-4"
        />

        {/* Run Button */}
        <button
          onClick={handleRunClick}
          className="bg-green-500 px-4 py-2 rounded hover:bg-green-600 transition mb-4"
        >
          {loading ? "Running" : coderun}
        </button>

        {/* AI Suggestions Button */}
        <button
          onClick={handleGetSuggestions}
          className="bg-purple-500 px-4 py-2 rounded hover:bg-purple-600 transition ml-2 mb-4"
        >
          Get AI Suggestions
        </button>

        {/* Output Section */}
        <div className="bg-[#2e2f3e] p-4 rounded mt-4">
          <h3 className="text-lg font-semibold mb-2">Output:</h3>
          <pre>{output}</pre>
        </div>

        {/* AI Suggestions Section */}
        <div className="bg-[#2e2f3e] p-4 rounded mt-4">
          <h3 className="text-lg font-semibold mb-2">AI Suggestions:</h3>
          <pre>{suggestion || "No suggestions available"}</pre>
        </div>
      </div>
    </div>
  );
};

export default Editor;
