import React, { useState } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';

const Aisuggest = ({ code }) => {
  const [suggestion, setSuggestion] = useState('');
  const [loading, setLoading] = useState(false);

  const fetchAICodeSuggestions = async (codeSnippet) => {
    setLoading(true);
    try {
      const response = await axios.post(
        'https://api.openai.com/v1/chat/completions',
        {
          model: 'gpt-4', // Or 'gpt-4' if available
          messages: [
            {
              role: 'user',
              content: `Suggest improvements or corrections for the following code: ${codeSnippet}`
            }
          ]
        },
        {
          headers: {
            Authorization: `Bearer `, // Insert your API key here
            'Content-Type': 'application/json'
          }
        }
      );

      const suggestionText = response.data.choices?.[0]?.message?.content || 'No suggestion available.';
      setSuggestion(suggestionText);
      toast.success('AI suggestion received!');
    } catch (error) {
      console.error('Error fetching AI suggestions:', error);
      toast.error('Failed to fetch AI suggestions. Check API key or limits.');
    } finally {
      setLoading(false);
    }
  };

  const handleGetSuggestions = () => {
    if (!code.trim()) {
      toast.error('Code editor is empty!');
      return;
    }
    fetchAICodeSuggestions(code);
  };

  return (
    <div className="bg-[#2e2f3e] p-4 rounded mt-4 text-white">
      <h3 className="text-lg font-semibold mb-2">AI Code Suggestions</h3>
      <button
        onClick={handleGetSuggestions}
        className="bg-blue-500 px-4 py-2 rounded hover:bg-blue-600 transition mb-4"
        disabled={loading}
      >
        {loading ? 'Fetching...' : 'Get AI Suggestions'}
      </button>
      {suggestion && (
        <div className="bg-[#1e1f29] p-3 rounded mt-2">
          <h4 className="text-md font-medium mb-1">Suggested Improvements:</h4>
          <pre>{suggestion}</pre>
        </div>
      )}
    </div>
  );
};

export default Aisuggest;
