import React, { useEffect, useState } from 'react';
import { FaRocketchat } from "react-icons/fa";
import toast from "react-hot-toast";
import socket from './Socket';
import { useLocation } from 'react-router-dom';

const Chat = () => {
  const location = useLocation();
  const [isOpen, setIsOpen] = useState(false);
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const { username, roomid } = location.state || {};

  const toggleChat = () => setIsOpen(!isOpen);

  const sendMessage = () => {
    if (message.trim()) {
      socket.emit("message", { message, username, roomid });
      setMessages((prevMessages) => [...prevMessages, { message, username }]);
      setMessage("");
      toast.success("Message sent");
    }
  };

  useEffect(() => {
    socket.on("new-message", (data) => {
      setMessages((prevMessages) => [...prevMessages, data]);
      if (data.username !== username) {
        toast.success(`Message received from ${data.username}`);
      }
    });

    return () => socket.off("new-message");
  }, [roomid, username]);

  return (
    <div className="fixed bottom-4 right-4 z-50">
      <button
        onClick={toggleChat}
        className="flex items-center px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 mb-2"
      >
        <FaRocketchat className="mr-2" /> {isOpen ? "Close Chat" : "Open Chat"}
      </button>

      {isOpen && (
        <div className="bg-white border border-gray-300 rounded-lg p-4 shadow-lg w-80 h-96 resize-y overflow-hidden">
          <h2 className="text-lg font-semibold text-blue-800">Chat Room</h2>
          <div className="h-64 overflow-y-auto mb-4 border rounded p-2">
            {messages.map((msg, index) => (
              <div key={index} className="mb-2">
                <strong className="text-blue-500">{msg.username}:</strong>
                <p className="text-black">{msg.message}</p>
              </div>
            ))}
          </div>
          <input
            type="text"
            placeholder="Write a message..."
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            className="w-full border border-gray-300 rounded p-2 mb-2"
          />
          <button
            onClick={sendMessage}
            className="w-full bg-green-500 text-white rounded py-2 hover:bg-green-600"
          >
            Send
          </button>
        </div>
      )}
    </div>
  );
};

export default Chat;
