import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { FaRocketchat } from "react-icons/fa";
import toast from "react-hot-toast";
import socket from './Socket';

const Chat = () => {
  const location = useLocation();
  const [chat, setChat] = useState(false);
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const { username, roomid } = location.state || {};

  const openChat = () => setChat(true);
  const closeChat = () => setChat(false);

  const sendMessage = () => {
    if (message.trim()) {
      socket.emit("message", { message, username, roomid });
      setMessage("");
      toast.success("Message sent");
    }
    setMessages((prevMessages) => [...prevMessages, { message, username }]);
    setMessage("");
  };

  const handleInputChange = (e) => setMessage(e.target.value);

  useEffect(() => {
    
    socket.on("new-message", (data) => {
      setMessages((prevMessages) => [...prevMessages, data]);
      toast.success(`Message received from ${data.username}`);
    });

    return () => socket.off("new-message");
  }, [roomid, username]);

  return (
    <div className="p-4">
      <button
        onClick={openChat}
        className="flex items-center px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
      >
        <FaRocketchat className="mr-2" /> Open Chat
      </button>

      {chat && (
        <div className="fixed inset-0 bg-gray-900 bg-opacity-75 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg w-full max-w-md p-4">
            <h2 className="text-xl font-semibold mb-4 text-blue-800">Chat Room</h2>
            <div className="mb-4 h-64 overflow-y-auto border border-gray-300 rounded p-2">
              {messages.map((msg, index) => (
                <div key={index} className="mb-2">
                  <strong className="text-blue-500">{msg.username}:</strong > <p className='text-black'> {msg.message}  </p>
                </div>
              ))}
            </div>
            <input
              type="text"
              placeholder="Write a message..."
              value={message}
              onChange={handleInputChange}
              className="w-full border text-black border-gray-300 rounded p-2 mb-2 bg-white" // Added bg-white
            />
            <button
              onClick={sendMessage}
              className="w-full bg-green-500 text-white rounded py-2 hover:bg-green-600"
            >
              Send
            </button>
            <button
              onClick={closeChat}
              className="w-full bg-red-500 text-white rounded py-2 mt-2 hover:bg-red-600"
            >
              Close Chat
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Chat;
