import React, { useEffect, useRef } from 'react';
import { AiOutlineAudio } from "react-icons/ai";
import { ZegoUIKitPrebuilt } from '@zegocloud/zego-uikit-prebuilt';
import { useLocation } from 'react-router-dom';

const Audiocall = () => {
  const location = useLocation();
  const { username, roomid } = location.state || {};
  const callContainerRef = useRef(null);

  const play = async () => {
    const appID = 1029814731; // Replace with your ZEGOCLOUD App ID
    const serverSecret = "487ec97a55397f781ce2a05b879b756c"; // Replace with your ZEGOCLOUD Server Secret
    const userID = String(roomid || "defaultRoomID"); // Convert to string
    const userName = String(username || "Guest"); // Convert to string

    // Generate Kit Token
    const kitToken = ZegoUIKitPrebuilt.generateKitTokenForProduction(appID, serverSecret, userID, userName);

    // Create ZegoUIKit instance
    const zc = ZegoUIKitPrebuilt.create(kitToken);
    
    // Join the room automatically
    zc.joinRoom({
      container: callContainerRef.current,
      sharedLinks: [
        {
          name: 'Personal link',
          url: window.location.protocol + '//' + 
                window.location.host + window.location.pathname +
                '?roomID=' + userID,
        },
      ],
      scenario: {
        mode: ZegoUIKitPrebuilt.GroupCall, // Group call mode
      },
      // Disable room ID input
      enableRoomIDInput: false,
      userID: userID,
      userName: userName,
    });
  };

  useEffect(() => {
    play(); // Automatically join the room on component mount
  }, [roomid, username]);

  return (
    <div>
      <div ref={callContainerRef} style={{ width: '100%', height: '80vh' }}></div>
      <button onClick={play}>
        <AiOutlineAudio /> Voice and Video Call
      </button>
    </div>
  );
};

export default Audiocall;
