import React from 'react';
import { AiOutlineAudio } from "react-icons/ai";
import { ZegoUIKitPrebuilt } from '@zegocloud/zego-uikit-prebuilt';
import { useLocation } from 'react-router-dom';

const Audiocall = () => {
  const location = useLocation();
  const { username, roomid } = location.state || {};

  const play = () => {
    const appID = 1029814731; // Replace with your ZEGOCLOUD App ID
    const serverSecret = "487ec97a55397f781ce2a05b879b756c"; // Replace with your ZEGOCLOUD Server Secret
    const userID = String(roomid || "defaultRoomID"); // Ensure userID is a string
    const userName = String(username || "Guest"); // Ensure userName is a string

    console.log("userID:", userID, "userName:", userName); // Debugging check

    try {
      if (ZegoUIKitPrebuilt && typeof ZegoUIKitPrebuilt.create === "function") {
        const zegoInstance = ZegoUIKitPrebuilt.create({
          appID,
          serverSecret,
          userID,
          userName,
          container: document.getElementById("call-container"),
          config: {
            turnOnMicrophoneWhenJoining: true,
            turnOnCameraWhenJoining: true,
            showMyCameraToggleButton: true,
            showMyMicrophoneToggleButton: true,
            showAudioVideoSettingsButton: true,
            showScreenSharingButton: true,
            showTextChat: true,
            showUserList: true,
            maxUsers: 50,
            layout: "Sidebar",
            showLayoutButton: true,
            scenario: {
              mode: "GroupCall",
              config: {
                role: "Host",
              },
            },
          },
        });

        if (zegoInstance && typeof zegoInstance.start === "function") {
          zegoInstance.start();
        } else {
          console.error("Failed to initialize ZegoUIKitPrebuilt instance.");
        }
      } else {
        console.error("ZegoUIKitPrebuilt.create is not a function. Check your SDK installation.");
      }
    } catch (error) {
      console.error("Error initializing ZEGOCLOUD call:", error);
    }
  };

  return (
    <div>
      <button onClick={play}>
        <AiOutlineAudio /> Voice and Video Call
      </button>
      <div id="call-container" style={{ width: '100%', height: '80vh' }}></div>
    </div>
  );
};

export default Audiocall;
