import React, { useEffect, useState } from 'react'
import { useLocation } from 'react-router-dom';
import { FaRocketchat } from "react-icons/fa";
import toast from "react-hot-toast";
import io from "socket.io-client";
const socket = io("http://localhost:4000");


const Chat = () => {
    const location=useLocation()
    const [chat,setchat]=useState(false)
    const [message,setmessage]=useState("")
    const [messages,setmessages]=useState([])
    const { username, roomid } = location.state || {};
    const chatbox=()=>{
          setchat(true)
    }
    const closechat=()=>{
        setchat(false)
    }
    const sendmessage=()=>{
        socket.emit("message",{message,username,roomid})
        toast.success("message sent")
    }
    const msg=(e)=>{
        setmessage(e.target.value)
    }
    useEffect(()=>{
        socket.on("new-message",(data)=>{
            setmessage((prevmessage)=>[...prevmessage,data])
            toast.success(`message received from ${username.data}`)
        })
    })
  return (
    <div>

        <button onClick={chatbox}> <FaRocketchat />  </button>
        {chat?(
            <div>
                <h1>Chat</h1>
                <input type='text' placeholder='write message here' value={message}
                onChange={msg}
                ></input>
                {messages.map((msg, index) => (
  <div key={index}><strong>{msg.username}:</strong> {msg.message}</div>
))}

                <button onClick={sendmessage}>Send</button>
                <button onClick={closechat}>close chat</button>
                </div>
        ):(
            <>
            </>

        )}
        
    </div>
  )
}

export default Chat
