import React from 'react';
import { ZegoUIKitPrebuilt } from '@zegocloud/zego-uikit-prebuilt';
import { useLocation } from 'react-router-dom';
import { MdOutlineVideoCall } from "react-icons/md";
const Audiocall = () => {
  const location = useLocation();
  const { username, roomid } = location.state || {};

  const play = () => {
    const appID = 1029814731; // Replace with your ZEGOCLOUD App ID
    const serverSecret = "487ec97a55397f781ce2a05b879b756c"; // Replace with your ZEGOCLOUD Server Secret
    const userID = roomid; // Unique ID for each user, can be generated or retrieved as needed
    const userName = username; // Display name of the user

    ZegoUIKitPrebuilt.createCall({
      appID,
      serverSecret,
      userID,
      userName,
      container: document.getElementById("call-container"), 
      config: {
        turnOnMicrophoneWhenJoining: true,
        turnOnCameraWhenJoining: true,
        showMyCameraToggleButton: true,
        showMyMicrophoneToggleButton: true,
        showAudioVideoSettingsButton: true,
        showScreenSharingButton: true,
        showTextChat: true,
        showUserList: true,
        maxUsers: 50,
        layout: "Sidebar",
        showLayoutButton: true,
        scenario: {
          mode: "GroupCall",
          config: {
            role: "Host",
          },
        },
      },
    });
    play.onError((error) => {
      console.error("Error during call setup:", error);
      alert("Failed to start the call. Please check your configuration.");
    });
    
  };

  return (
    <div>
      <button onClick={play}>
      <MdOutlineVideoCall /> Video Call
      </button>
      {/* This container is where the ZEGOCLOUD UI will render */}
      <div id="call-container" style={{ width: '100%', height: '80vh' }}></div>
    </div>
  );
};

export default Audiocall;
