import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import uuid from 'react-uuid';
import toast from "react-hot-toast";

const Home = () => {
    const navigate=useNavigate()
    let [roomid,setroomid]=useState("")
    let [username,setusername]=useState("")
    function roomidchange(e)
    {
        setroomid(e.target.value)     
    }
    function usernamechange(e)
    {
        setusername(e.target.value)
    }
    function generateid()
    {
            roomid=""
            let x=uuid()
            setroomid(x)
            toast.success("new Room created successfully")
    }
    function joinroom(event)
    {
        event.preventDefault()
        if(!roomid||!username)
            {
                toast.error("Both the field are requried ")
                return
            }  
            navigate(`/editor/${roomid}`)
            toast.success("Entered in the room")
        
   
    }
    const handleenter=(e)=>{
        if(e.code==="Enter")
        {
            joinroom();
        }
    }
  return (
    <div>
        This is home page
        <div>
            <img src="Images\codecast.png" alt='codecollab'></img>
            <h1>enter the room id</h1>
            <input type='text' placeholder='Enter Room Id' className='text-black' onChange={roomidchange} value={roomid} onkey></input><br></br>
            <input type='text' placeholder='Username' className='text-black' onChange={usernamechange}value={username} onKeyUp={handleenter}></input><br></br>
            <button onClick={joinroom}>Join</button>
            <p>Dont't have Room id ? create <span className="text-green-600 cursor-pointer" onClick={generateid} >New Room Id</span></p>
        </div>
    </div>
  )
}
export default Home
