import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { CopyToClipboard } from 'react-copy-to-clipboard';
import toast from "react-hot-toast";
import Avatar from 'react-avatar';
import { UnControlled as CodeMirror } from "react-codemirror2";
import "codemirror/lib/codemirror.css";
import "codemirror/mode/javascript/javascript";
import "codemirror/addon/hint/show-hint";
import "codemirror/addon/hint/javascript-hint";
import "codemirror/addon/edit/closebrackets";
import "codemirror/addon/edit/closetag";
import "codemirror/theme/dracula.css";
import "codemirror/addon/lint/lint.css";
import "codemirror/addon/lint/lint";
import "codemirror/addon/lint/javascript-lint";
import * as eslint from "eslint";  // Import ESLint directly

const Editor = () => {
  const location=useLocation();
  const {username,roomid}=location.state||{}
  const navigate=useNavigate()

  function copyid() {
    toast.success("Text Copied to Clipboard!")
  }

  const Leaveroom = () => {
    navigate("/")
    toast.success("Left the room")
  }

  return (
    <>
      <div>
        <img src="" alt="codecast"></img>
        <h2>Members</h2>
        <h3><Avatar name={username.toString()} size={100} maxInitials={2} round={true}/></h3> 
        <h3>{username}</h3>
        <CopyToClipboard text={roomid} onCopy={copyid}>
          <button>Copy Room Id</button>
        </CopyToClipboard><br></br>
        <button onClick={Leaveroom}>Leave Room</button>
      </div>
      <div>
        <CodeMirror 
          options={{
            theme: "dracula",
            lint: {
              "esversion": 6,      // Add ES6 support if needed
              "undef": true,       // Flags usage of undefined variables
              "globals": {         // Define global variables (optional)
                "React": true,
                "module": true
              }
            },
            lineNumbers: true,
            mode: "javascript",
            extraKeys: { "Ctrl-Space": "autocomplete" },
            autoCloseBrackets: true,
            autoCloseTags: true,
          }}
        />
      </div>
    </>
  );
};

export default Editor;
